import React, { useState } from 'react';
import api from '../api/axiosInstance';

export default function ClientForm({ onSubmit, formValues, setFormValues }) {
  const [error, setError] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // validaciones básicas
    if (!formValues.nombre?.trim()) return setError('El nombre es obligatorio');
    if (formValues.saldoAnterior === '' || formValues.montoCompras === '' || formValues.pagoRealizado === '') return setError('Completa todos los campos numéricos');

    const payload = {
      nombre: formValues.nombre.trim(),
      saldoAnterior: Number(formValues.saldoAnterior),
      montoCompras: Number(formValues.montoCompras),
      pagoRealizado: Number(formValues.pagoRealizado)
    };

    if (Number.isNaN(payload.saldoAnterior) || Number.isNaN(payload.montoCompras) || Number.isNaN(payload.pagoRealizado)) {
      return setError('Valores numéricos inválidos');
    }

    try {
      setSubmitting(true);
      const res = await api.post('/clientes', payload);
      const created = res.data ?? res;

      // Si el backend no devuelve el nombre dentro del resultado, forzarlo desde el payload
      if (!created || typeof created !== 'object') {
        // fallback: pasar algo útil al padre
        const fallback = { ...payload, id: created?.id ?? null, saldoActual: created?.saldoActual ?? null };
        if (onSubmit) onSubmit(fallback);
      } else {
        if (!created.nombre || String(created.nombre).trim() === '') {
          created.nombre = payload.nombre;
        }
        if (onSubmit) onSubmit(created);
      }

      // limpiar formulario
      setFormValues({
        nombre: '',
        saldoAnterior: '',
        montoCompras: '',
        pagoRealizado: ''
      });
    } catch (err) {
      console.error('POST /clientes error', err);
      const msg = err.response?.data?.message || err.message || 'Error guardando';
      setError(msg);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="card">
      <h3>Ingresar Datos</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <label>Nombre del cliente:</label>
          <input
            name="nombre"
            className="input-large"
            type="text"
            value={formValues.nombre || ""}
            onChange={(e) => setFormValues({ ...formValues, nombre: e.target.value })}
          />
        </div>

        <div className="form-row">
          <label>Saldo anterior:</label>
          <input
            name="saldoAnterior"
            className="input-price"
            type="number"
            step="0.01"
            value={formValues.saldoAnterior || ""}
            onChange={(e) => setFormValues({ ...formValues, saldoAnterior: e.target.value })}
          />
        </div>

        <div className="form-row">
          <label>Monto de compras:</label>
          <input
            name="montoCompras"
            className="input-price"
            type="number"
            step="0.01"
            value={formValues.montoCompras || ""}
            onChange={(e) => setFormValues({ ...formValues, montoCompras: e.target.value })}
          />
        </div>

        <div className="form-row">
          <label>Pago realizado:</label>
          <input
            name="pagoRealizado"
            className="input-price"
            type="number"
            step="0.01"
            value={formValues.pagoRealizado || ""}
            onChange={(e) => setFormValues({ ...formValues, pagoRealizado: e.target.value })}
          />
        </div>

        <div className="form-actions">
          <button type="submit" className="btn-primary" disabled={submitting}>
            {submitting ? 'Guardando...' : 'Calcular Cliente'}
          </button>
        </div>

        {error && <div className="muted">Error: {String(error)}</div>}
      </form>
    </div>
  );
}
