import React, { useState } from 'react';
import api from '../api/axiosInstance';

export default function ClientForm({ onSaved }) {
  const [nombre, setNombre] = useState('');
  const [saldoAnterior, setSaldoAnterior] = useState('');
  const [montoCompras, setMontoCompras] = useState('');
  const [pagoRealizado, setPagoRealizado] = useState('');
  const [error, setError] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // validaciones básicas
    if (!nombre.trim()) return setError('El nombre es obligatorio');
    if (saldoAnterior === '' || montoCompras === '' || pagoRealizado === '') return setError('Completa todos los campos numéricos');

    const payload = {
      nombre: nombre.trim(),
      saldoAnterior: Number(saldoAnterior),
      montoCompras: Number(montoCompras),
      pagoRealizado: Number(pagoRealizado)
    };

    if (Number.isNaN(payload.saldoAnterior) || Number.isNaN(payload.montoCompras) || Number.isNaN(payload.pagoRealizado)) {
      return setError('Valores numéricos inválidos');
    }

    try {
      setSubmitting(true);
      const res = await api.post('/clientes', payload);
      const created = res.data ?? res;

      // Si el backend no devuelve el nombre dentro del resultado, forzarlo desde el payload
      if (!created || typeof created !== 'object') {
        // fallback: pasar algo útil al padre
        const fallback = { ...payload, id: created?.id ?? null, saldoActual: created?.saldoActual ?? null };
        if (onSaved) onSaved(fallback);
      } else {
        if (!created.nombre || String(created.nombre).trim() === '') {
          created.nombre = payload.nombre;
        }
        if (onSaved) onSaved(created);
      }

      // limpiar formulario
      setNombre('');
      setSaldoAnterior('');
      setMontoCompras('');
      setPagoRealizado('');
    } catch (err) {
      console.error('POST /clientes error', err);
      const msg = err.response?.data?.message || err.message || 'Error guardando';
      setError(msg);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="card">
      <h3>Ingresar Datos</h3>
      <form onSubmit={handleSubmit}>
        <label>Nombre del cliente:</label>
        <input value={nombre} onChange={e => setNombre(e.target.value)} required />
        <label>Saldo anterior:</label>
        <input type="number" step="0.01" value={saldoAnterior} onChange={e => setSaldoAnterior(e.target.value)} required />
        <label>Monto de compras:</label>
        <input type="number" step="0.01" value={montoCompras} onChange={e => setMontoCompras(e.target.value)} required />
        <label>Pago realizado:</label>
        <input type="number" step="0.01" value={pagoRealizado} onChange={e => setPagoRealizado(e.target.value)} required />
        <button type="submit" disabled={submitting}>{submitting ? 'Guardando...' : 'Calcular Cliente'}</button>
        {error && <div className="muted">Error: {String(error)}</div>}
      </form>
    </div>
  );
}
