// ...new file...
import React from 'react';
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
} from 'chart.js';
import { Doughnut, Bar } from 'react-chartjs-2';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title);

function toNum(v) {
  return Number(v ?? 0) || 0;
}
function isMoroso(item) {
  return item?.esMoroso === true || item?.moroso === true ||
    String(item?.esMoroso).toLowerCase() === 'sí' ||
    String(item?.esMoroso).toLowerCase() === 'si' ||
    item?.esMoroso === 'true' || item?.moroso === 'true' ||
    (typeof item?.moroso === 'string' && item.moroso.toLowerCase() === 'sí');
}

export default function StatsPanel({ results = [] }) {
  const total = results.length;
  const morososList = results.filter(isMoroso);
  const morosos = morososList.length;
  const noMorosos = Math.max(0, total - morosos);
  const morososPercent = total > 0 ? (morosos / total) * 100 : 0;

  const sumSaldos = results.reduce((s, r) => s + toNum(r.saldoActual ?? r.saldo ?? r.saldoAnterior), 0);
  const sumInteresMorosos = morososList.reduce((s, r) => s + toNum(r.interes ?? r.interest ?? 0), 0);
  const sumSaldoMorosos = morososList.reduce((s, r) => s + toNum(r.saldoActual ?? r.saldo ?? r.saldoAnterior), 0);

  const avgSaldoMorosos = morosos > 0 ? sumSaldoMorosos / morosos : 0;
  const avgInteresMorosos = morosos > 0 ? sumInteresMorosos / morosos : 0;

  const format = (n) => Number(n || 0).toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 });

  const doughnutData = {
    labels: ['Morosos', 'No morosos'],
    datasets: [
      {
        data: [morosos, noMorosos],
        backgroundColor: ['#2563eb', '#cfe4ff'],
        hoverBackgroundColor: ['#1e4fc4', '#b7dbff'],
        borderWidth: 0,
      },
    ],
  };

  const barData = {
    labels: ['Promedio (morosos)'],
    datasets: [
      {
        label: 'Saldo promedio',
        data: [avgSaldoMorosos],
        backgroundColor: '#3b82f6',
      },
      {
        label: 'Interés promedio',
        data: [avgInteresMorosos],
        backgroundColor: '#60a5fa',
      },
    ],
  };

  const barOptions = {
    responsive: true,
    plugins: {
      legend: { position: 'top' },
      title: { display: false },
    },
    scales: {
      x: { stacked: false },
      y: {
        beginAtZero: true,
        ticks: { callback: (v) => Number(v).toLocaleString() },
      },
    },
  };

  const exportPDF = () => {
    const html = `
      <html>
        <head>
          <title>Estadísticas - Banco Peluche</title>
          <style>
            body { font-family: Arial, sans-serif; padding: 24px; color:#012a56; }
            h2 { color:#0357a6; }
            table { width:100%; border-collapse: collapse; margin-top:12px; }
            td, th { padding:8px 10px; border-bottom:1px solid #e6edf3; text-align:left; }
            .chart { margin-top:12px; }
          </style>
        </head>
        <body>
          <h2>Estadísticas - Banco “Bandido de Peluche”</h2>
          <table>
            <tr><th>Total Clientes</th><td>${total}</td></tr>
            <tr><th>Morosos (cantidad)</th><td>${morosos}</td></tr>
            <tr><th>% Morosos</th><td>${morososPercent.toFixed(2)}%</td></tr>
            <tr><th>Promedio saldo (morosos)</th><td>${format(avgSaldoMorosos)}</td></tr>
            <tr><th>Promedio interés (morosos)</th><td>${format(avgInteresMorosos)}</td></tr>
            <tr><th>Suma de saldos (todos)</th><td>${format(sumSaldos)}</td></tr>
          </table>
          <div class="chart">
            <p><strong>Morosos vs No morosos</strong></p>
            <div>Morosos: ${morosos} — No morosos: ${noMorosos}</div>
          </div>
        </body>
      </html>
    `;
    const w = window.open('', '_blank');
    if (!w) return alert('Permite ventanas emergentes para exportar PDF.');
    w.document.write(html);
    w.document.close();
    w.focus();
    setTimeout(() => w.print(), 300);
  };

  return (
    <div className="card stats-panel-large">
      <div className="stats-grid" style={{ alignItems: 'center' }}>
        <div className="stats-chart" style={{ padding: 8 }}>
          <Doughnut data={doughnutData} options={{ responsive: true, plugins: { legend: { position: 'bottom' } } }} />
        </div>

        <div className="stats-info">
          <h3>Estadísticas</h3>
          <div className="stats-list">
            <div><strong>Total clientes:</strong> <span>{total}</span></div>
            <div><strong>Morosos:</strong> <span>{morosos} ({morososPercent.toFixed(2)}%)</span></div>
            <div><strong>Promedio saldo (morosos):</strong> <span>{format(avgSaldoMorosos)}</span></div>
            <div><strong>Promedio interés (morosos):</strong> <span>{format(avgInteresMorosos)}</span></div>
            <div><strong>Suma de saldos:</strong> <span>{format(sumSaldos)}</span></div>
          </div>

          <div style={{ marginTop: 12, display: 'flex', gap: 8, alignItems: 'center' }}>
            <button className="btn-primary" onClick={exportPDF}>Exportar PDF</button>
          </div>
        </div>

        <div style={{ gridColumn: '1 / -1', marginTop: 18 }}>
          <Bar data={barData} options={barOptions} />
        </div>
      </div>
    </div>
  );
}
